using ICD.Connect.Calendaring.Microsoft.Office365.Responses;
using Newtonsoft.Json;
using NUnit.Framework;

namespace ICD.Connect.Calendaring.Microsoft.Tests.Office365.Converters
{
	[TestFixture]
	public sealed class TokenResponseConverterTest
	{
		[Test]
		public void DeserializeTest()
		{
			const string json = @"		{
			""token_type"": ""Bearer"",
			""expires_in"": 3600,
			""ext_expires_in"": 1800,
			""access_token"": ""..LJmvC4q11jviTSLBVP9VfwXT3cysS08QSBQhDb_UuhkmW8NZ3FqQc54scnFj3I8v2wSdHToQb__uDP_IolPxPJz6sUI4sSJJp_yXKpbDJj6v-6-HeYV0bNyrsu8W4x0nCnqu_0-kuhpYiZJ_ofEx01G5Q8aqCERokBy1AL8kYgPxc1i7uesWWEqS2J0qVj-e9zNm9Go3guKQZ3q8dN60WPPuFkxr3zOTAG3fk10Ka6_BSFsPau28ylSqDZkdF7I0_Dp-veZ6Rs6ICm98Js-Hq7sq99W9kmiM2_6f17-7BS_2nCqIy-9CtjYbWTfOq_8Ii527bBHDd1VCb79XFQw3sQ""
		}";

			TokenResponse response = JsonConvert.DeserializeObject<TokenResponse>(json);

			Assert.AreEqual("Bearer", response.TokenType);
			Assert.AreEqual(3600, response.ExpiresInSeconds);
			Assert.AreEqual(1800, response.ExtensionExpiresInSeconds);
			Assert.AreEqual("..LJmvC4q11jviTSLBVP9VfwXT3cysS08QSBQhDb_UuhkmW8NZ3FqQc54scnFj3I8v2wSdHToQb__uDP_IolPxPJz6sUI4sSJJp_yXKpbDJj6v-6-HeYV0bNyrsu8W4x0nCnqu_0-kuhpYiZJ_ofEx01G5Q8aqCERokBy1AL8kYgPxc1i7uesWWEqS2J0qVj-e9zNm9Go3guKQZ3q8dN60WPPuFkxr3zOTAG3fk10Ka6_BSFsPau28ylSqDZkdF7I0_Dp-veZ6Rs6ICm98Js-Hq7sq99W9kmiM2_6f17-7BS_2nCqIy-9CtjYbWTfOq_8Ii527bBHDd1VCb79XFQw3sQ", response.AccessToken);
		}
	}
}
